import React, { Component } from "react";
import ListOfPlants from "../../components/ListOfPlants/ListOfPlants";
import StatusBar from "../../components/StatusBar/StatusBar";
// import axios from "axios";

export default class Home extends Component {
  state = {
    plantInfo: [],
  };
  componentDidMount() {
    this.setState({
      plantInfo: [
        {
          timestamp: 1618274087486,
          plantName: "Name1",
          id: 1,
          plantStatus: "Must Water",
        },
        {
          timestamp: 1618274087486,
          plantName: "Name2",
          id: 2,
          plantStatus: "Must Water",
        },
        {
          timestamp: 1618252487486,
          plantName: "Name3",
          id: 3,
          plantStatus: "Must Water",
        },
        {
          timestamp: 1618274087486,
          plantName: "Name4",
          id: 4,
          plantStatus: "Must Water",
        },
        {
          timestamp: 1618274087486,
          plantName: "Name5",
          id: 5,
          plantStatus: "Must Water",
        },
      ],
    });
    // axios
    //   .get("https://localhost:5001/api/SVPlants")
    //   .then((res) =>
    //     this.setState({
    //       plantInfo: res.data,
    //     })
    //   )
    //   .catch((err) => console.error(err));
  }
  buttonClicks = (time, id, status) => {
    const time_subtraction = Date.now() - time;
    // do an enumerate for the status options
    if (time_subtraction < 30000 && status === "Just Watered") {
      // this.setState((prevState) => ({
      //   plantInfo: prevState.plantInfo.map((el) =>
      //     el.id === id ? { ...el, timestamp: Date.now() } : el
      //   ),
      return null;
      // }));
      // console.log(this.state.plantInfo.find(o=>o.id===id).plantStatus);
      // console.log("Watering");
    } else if (time_subtraction > 21600000) {
      this.setState((prevState) => ({
        plantInfo: prevState.plantInfo.map((el) =>
          el.id === id
            ? { ...el, timestamp: Date.now(), plantStatus: "Just Watered" }
            : el
        ),
      }));
      console.log(this.state.plantInfo.find((o) => o.id === id).plantStatus);
      console.log("Must Water");
    } else if (time_subtraction < 10000 && status === "Watering") {
      this.setState((prevState) => ({
        plantInfo: prevState.plantInfo.map((el) =>
          el.id === id
            ? { ...el, timestamp: Date.now(), plantStatus: "Just Watered" }
            : el
        ),
      }));
// ///////////////////////////////////////////////////////////////////////////
      // SET INTERVAL SEQUENCE

      // const toSetState = () => {
      //   this.setState((prevState) => ({
      //     plantInfo: prevState.plantInfo.map((el) =>
      //       el.id === id
      //         ? { ...el, timestamp: Date.now(), plantStatus: "Just Watered" }
      //         : el
      //     ),
      //   }));
      //   console.log(this.state.plantInfo.find((o) => o.id === id).plantStatus);
      // };
      // setInterval(toSetState, 10000);
      // console.log(this.state.plantInfo.find((o) => o.id === id).plantStatus);
/////////////////////////////////////////////////////
    } else {
      this.setState((prevState) => ({
        plantInfo: prevState.plantInfo.map((el) =>
          el.id === id
            ? { ...el, timestamp: Date.now(), plantStatus: "Watering" }
            : el
        ),
      }));
      // console.log(this.state.plantInfo.find((o) => o.id === id).plantStatus);
      // console.log("Water");
    }
  };
  buttonLook = (time, id) => {
    const time_subtraction = Date.now() - time;
    if (time === 1618274087486) {
      return (
        <button
          // disabled
          style={{
            backgroundColor: "pink",
            borderRadius: 5,
            border: "none",
            outline: "none",
            fontWeight: "bold",
            color: "white",
            width: "100%",
            fontSize: "20px",
            height: "30px",
          }}
          onClick={() => this.buttonClicks(time, id)}
        >
          Start Watering
        </button>
      );
    } else if (time_subtraction > 21600000) {
      return (
        <button
          style={{
            backgroundColor: "red",
            borderRadius: 5,
            border: "none",
            outline: "none",
            fontWeight: "bold",
            color: "white",
            width: "100%",
            fontSize: "13.5px",
            height: "30px",
          }}
          onClick={() => this.buttonClicks(time, id)}
        >
          Start Watering
        </button>
      );
    } else {
      return (
        <button
          style={{
            backgroundColor: "black",
            borderRadius: 5,
            border: "none",
            outline: "none",
            fontWeight: "bold",
            color: "white",
            width: "100%",
            fontSize: "20px",
            height: "30px",
          }}
          onClick={() => this.buttonClicks(time, id)}
        >
          Water
        </button>
      );
    }
  };

  render() {
    return (
      <div>
        <StatusBar />
        <div style={{ display: "flex", justifyContent: "space-evenly" }}>
          {this.state.plantInfo.map((plant) => (
            <ListOfPlants
              couler={this.state.couler}
              buttonLook={this.buttonLook}
              buttonClicks={this.buttonClicks}
              plant={plant}
            />
          ))}
        </div>
      </div>
    );
  }
}
