import React, { useState } from "react";
import { useDispatch, useSelector } from 'react-redux'

import planty from "../../media/plant.png";

export default function Plants({
  plant,
}) {
    const pl = useSelector(state =>
    state.plants.find(p => p.id === plant.id)
  )
  const [plantName, setPlantName] = useState(plant.plantName)
  const [plantStatus, setPlantStatus] = useState(plant.plantStatus)
  const [timestamp, setTimestamp] = useState(plant.timestamp)

  const dispatch = useDispatch()
  const history = useHistory()

    const buttonClicks = (time, id, status, name) => {
            // const aPlant = {
    //   plantName: "Plant 1",
    //   plantStatus: "MustWater",
    //   timestamp: 1234567890,
    // };
    // axios
    //   .put("http://localhost:2238/api/SVPlantItems/1", aPlant)
    //   .then((response) => console.log(response));

    const time_subtraction = Date.now() - time;
    // do an enumerate for the status options
    if (time_subtraction < 30000 && status === "Just Watered") {
      return null;
    } else if (status === "Must Water" || status === "Water") {
        const onPlantNameChanged = setPlantName(name)
        const onPlantStatusChanged = setPlantStatus("Watering")
        const onTimestampChanged = setTimestamp(Date.now())
        dispatch(plantUpdated({ id: plantId, plantName: name, plantStatus: "Watering", timestamp: Date.now() }))
        //   this.setState({
    //     plantInfo: this.state.plantInfo.map((el) =>
    //       el.id === id
    //         ? {
    //             id: el.id,
    //             plantName: el.plantName,
    //             timestamp: Date.now(),
    //             plantStatus: "Watering",
    //           }
    //         : el
    //     ),
    //   });
      const tenSecondInterval = () => {
        this.setState({
          plantInfo: this.state.plantInfo.map((el) =>
            el.id === id
              ? {
                  id: el.id,
                  plantName: el.plantName,
                  timestamp: Date.now(),
                  plantStatus: "Just Watered",
                }
              : el
          ),
        });

        const toSetState = () => {
          this.setState({
            plantInfo: this.state.plantInfo.map((el) =>
              el.id === id
                ? {
                    id: el.id,
                    plantName: el.plantName,
                    timestamp: Date.now(),
                    plantStatus: "Water",
                  }
                : el
            ),
          });
        };

        setTimeout(toSetState, 30000);
      };
      clearTimeoutVariable = setTimeout(tenSecondInterval, 10000);
    } else if (time_subtraction < 10000 && status === "Watering") {
      clearTimeout(clearTimeoutVariable);
      this.setState({
        plantInfo: this.state.plantInfo.map((el) =>
          el.id === id
            ? {
                id: el.id,
                plantName: el.plantName,
                timestamp: Date.now(),
                plantStatus: "Just Watered",
              }
            : el
        ),
      });

      const toSetState = () => {
        this.setState({
          plantInfo: this.state.plantInfo.map((el) =>
            el.id === id
              ? {
                  id: el.id,
                  plantName: el.plantName,
                  timestamp: Date.now(),
                  plantStatus: "Water",
                }
              : el
          ),
        });
      };
      setTimeout(toSetState, 30000);
    } else {
      this.setState({
        plantInfo: this.state.plantInfo.map((el) =>
          el.id === id
            ? {
                id: el.id,
                plantName: el.plantName,
                timestamp: Date.now(),
                plantStatus: "Watering",
              }
            : el
        ),
      });
    }
  };

  const buttonStyle = (status) => {
    if (status === "Water") {
      return "pink";
    } else if (status === "Watering") {
      return "purple";
    } else if (status === "Must Water") {
      return "red";
    } else if (status === "Just Watered") {
      return "green";
    }
  };
  return (
    <div
      style={{ display: "flex", flexDirection: "column", alignItems: "center" }}
    >
      <img
        src={planty}
        alt="a plant"
        style={{
          width: "100px",
          backgroundColor: "#faf0e6",
          borderRadius: 10,
        }}
      />
      <h3>{plant.plantName}</h3>
      <button
        style={{
          backgroundColor: buttonStyle(plant.plantStatus),
          borderRadius: 5,
          border: "none",
          outline: "none",
          fontWeight: "bold",
          color: "white",
          width: "100%",
          fontSize: "20px",
          height: "30px",
        }}
        onClick={() =>
          buttonClicks(plant.timestamp, plant.id, plant.plantStatus)
        }
      >
        {plant.plantStatus}
      </button>
    </div>
  );
}
