import React, { useEffect } from 'react'
import { useSelector, useDispatch } from 'react-redux'
// omit other imports
import { selectAllPlants, fetchPlants } from './plantsSlice'
import Plants from './Plants'

export const PlantsList = () => {
  const dispatch = useDispatch()
  const plants = useSelector((state) => state.plants)

  const plantStatus = useSelector(state => state.plants.status)

  useEffect(() => {
    if (plantStatus === 'idle') {
      dispatch(fetchPosts())
    }
  }, [plantStatus, dispatch])

  const renderedPlants = plants.map((plant) => <Plants plant={plant} />)

  return (
    <section>
      <h2>Plants</h2>
      <div style={{display:"flex", justifyContent: "space-evenly", flexWrap: "wrap", margin: "30px"}}>{renderedPlants}</div>
    </section>
  )
}
