import React from 'react'
import { useDispatch, useSelector } from 'react-redux'
import planty from "../../media/plant.png";

import { useHistory } from 'react-router-dom'

import { plantUpdated } from './plantsSlice'

let clearTimeoutVariable
export const PlantsList = () => {
  const plants = useSelector(state => state.plants)



    const buttonClicks = (time, id, status, name) => {
        const plant = useSelector(state =>
            state.plants.find(plant => plant.id === id)
          )
          const [plantName, setPlantName] = useState(plant.plantName)
          const [plantStatus, setPlantStatus] = useState(plant.plantStatus)
          const [timestamp, setTimestamp] = useState(plant.timestamp)

          const dispatch = useDispatch()
          const history = useHistory()
            // const aPlant = {
    //   plantName: "Plant 1",
    //   plantStatus: "MustWater",
    //   timestamp: 1234567890,
    // };
    // axios
    //   .put("http://localhost:2238/api/SVPlantItems/1", aPlant)
    //   .then((response) => console.log(response));

    const time_subtraction = Date.now() - time;
    // do an enumerate for the status options
    if (time_subtraction < 30000 && status === "Just Watered") {
      return null;
    } else if (status === "Must Water" || status === "Water") {

    //   this.setState({
    //     plantInfo: this.state.plantInfo.map((el) =>
    //       el.id === id
    //         ? {
    //             id: el.id,
    //             plantName: el.plantName,
    //             timestamp: Date.now(),
    //             plantStatus: "Watering",
    //           }
    //         : el
    //     ),
    //   });
      const tenSecondInterval = () => {
        this.setState({
          plantInfo: this.state.plantInfo.map((el) =>
            el.id === id
              ? {
                  id: el.id,
                  plantName: el.plantName,
                  timestamp: Date.now(),
                  plantStatus: "Just Watered",
                }
              : el
          ),
        });

        const toSetState = () => {
          this.setState({
            plantInfo: this.state.plantInfo.map((el) =>
              el.id === id
                ? {
                    id: el.id,
                    plantName: el.plantName,
                    timestamp: Date.now(),
                    plantStatus: "Water",
                  }
                : el
            ),
          });
        };

        setTimeout(toSetState, 30000);
      };
      clearTimeoutVariable = setTimeout(tenSecondInterval, 10000);
    } else if (time_subtraction < 10000 && status === "Watering") {
      clearTimeout(clearTimeoutVariable);
      this.setState({
        plantInfo: this.state.plantInfo.map((el) =>
          el.id === id
            ? {
                id: el.id,
                plantName: el.plantName,
                timestamp: Date.now(),
                plantStatus: "Just Watered",
              }
            : el
        ),
      });

      const toSetState = () => {
        this.setState({
          plantInfo: this.state.plantInfo.map((el) =>
            el.id === id
              ? {
                  id: el.id,
                  plantName: el.plantName,
                  timestamp: Date.now(),
                  plantStatus: "Water",
                }
              : el
          ),
        });
      };
      setTimeout(toSetState, 30000);
    } else {
      this.setState({
        plantInfo: this.state.plantInfo.map((el) =>
          el.id === id
            ? {
                id: el.id,
                plantName: el.plantName,
                timestamp: Date.now(),
                plantStatus: "Watering",
              }
            : el
        ),
      });
    }
  };


  const renderedPlants = plants.map(plant => (
    <div
    style={{ display: "flex", flexDirection: "column", alignItems: "center" }}
  >
    <img
      src={planty}
      alt="a plant"
      style={{
        width: "100px",
        backgroundColor: "#faf0e6",
        borderRadius: 10,
      }}
    />
    <h3>{plant.plantName}</h3>
    <button
      style={{
        // backgroundColor: buttonStyle(plant.plantStatus),
        borderRadius: 5,
        border: "none",
        outline: "none",
        fontWeight: "bold",
        color: "white",
        width: "100%",
        fontSize: "20px",
        height: "30px",
      }}
    //   onClick={() =>
    //     buttonClicks(plant.timestamp, plant.id, plant.plantStatus)
    //   }
    >
      {plant.plantStatus}
    </button>
  </div>

  ))

  return (
    <section>
      <h2>Plants</h2>
      {renderedPlants}
    </section>
  )
}
