import React from "react";
import planty from "../../media/plant.png";

export default function ListOfPlants({ plant, buttonClicks }) {
  const color = (time, id) => {
    const time_subtraction = Date.now() - time;
    if (time === 1618274087486) {
      return (
        <button
          // disabled
          style={{
            backgroundColor: "pink",
            borderRadius: 5,
            border: "none",
            outline: "none",
            fontWeight: "bold",
            color: "white",
            width: "100%",
            fontSize: "20px",
            height: "30px",
          }}
          onClick={() => buttonClicks(1)}
        >
          Start Watering
        </button>
      );
    } else if (time_subtraction > 21600000) {
      return (
        <button
          style={{
            backgroundColor: "red",
            borderRadius: 5,
            border: "none",
            outline: "none",
            fontWeight: "bold",
            color: "white",
            width: "100%",
            fontSize: "13.5px",
            height: "30px",
          }}
          onClick={() => buttonClicks(1)}
        >
          Start Watering
        </button>
      );
    } else {
      return (
        <button
          style={{
            backgroundColor: "black",
            borderRadius: 5,
            border: "none",
            outline: "none",
            fontWeight: "bold",
            color: "white",
            width: "100%",
            fontSize: "20px",
            height: "30px",
          }}
          onClick={() => buttonClicks(1)}
        >
          Water
        </button>
      );
    }
  };
  return (
    <div
      style={{ display: "flex", flexDirection: "column", alignItems: "center" }}
    >
      <img
        src={planty}
        alt="a plant"
        style={{
          width: "100px",
          backgroundColor: "#faf0e6",
          borderRadius: 10,
        }}
      />
      <h3>{plant.plantName}</h3>
      {color(plant.timestamp)}
    </div>
  );
}
