import React from "react";
import planty from "../../media/plant.png";

export default function ListOfPlants({ plant }) {
  const color = (time) => {
    const time_subtraction = Date.now() - time;
    if(time === Date.now()){
      return "green"
    } else if (time_subtraction > 21600000) {
      return "red"
    } else {
      return "black"
    }
  };
  const buttonClicks = (z) => {
    if( z === 1 ){
      console.log("tesdt")
      return "Watering"
    }else if (z === 2){
      return "Must Water"
    } else {
      return "Water";
    }
  };
  return (
    <div
      style={{ display: "flex", flexDirection: "column", alignItems: "center" }}
    >
      <img
        src={planty}
        alt="a plant"
        style={{
          width: "100px",
          backgroundColor: "#faf0e6",
          borderRadius: 10,
        }}
      />
      <h3>{plant.plantName}</h3>
      <button onClick={() => buttonClicks(1)}>{buttonClicks(2)}</button>
    </div>
  );
}
