import React from 'react'
// import {
//   BrowserRouter as Router,
//   Switch,
//   Route,
//   Redirect,
// } from 'react-router-dom'

// import { Navbar } from './app/Navbar'
import Plants from "./features/plants/Plants";
import StatusBar from "./app/StatusBar";
// import axios from "axios";
// import React, { Component } from "react";

let clearTimeoutVariable;

function App() {
  const buttonClicks = (time, id, status) => {
    const aPlant = {
      plantName: "Plant 1",
      plantStatus: "MustWater",
      timestamp: 1234567890,
    };
    // axios
    //   .put("http://localhost:2238/api/SVPlantItems/1", aPlant)
    //   .then((response) => console.log(response));

    const time_subtraction = Date.now() - time;
    // do an enumerate for the status options
    if (time_subtraction < 30000 && status === "Just Watered") {
      return null;
    } else if (status === "Must Water" || status === "Water") {
      this.setState({
        plantInfo: this.state.plantInfo.map((el) =>
          el.id === id
            ? {
                id: el.id,
                plantName: el.plantName,
                timestamp: Date.now(),
                plantStatus: "Watering",
              }
            : el
        ),
      });
      const tenSecondInterval = () => {
        this.setState({
          plantInfo: this.state.plantInfo.map((el) =>
            el.id === id
              ? {
                  id: el.id,
                  plantName: el.plantName,
                  timestamp: Date.now(),
                  plantStatus: "Just Watered",
                }
              : el
          ),
        });

        const toSetState = () => {
          this.setState({
            plantInfo: this.state.plantInfo.map((el) =>
              el.id === id
                ? {
                    id: el.id,
                    plantName: el.plantName,
                    timestamp: Date.now(),
                    plantStatus: "Water",
                  }
                : el
            ),
          });
        };

        setTimeout(toSetState, 30000);
      };
      clearTimeoutVariable = setTimeout(tenSecondInterval, 10000);
    } else if (time_subtraction < 10000 && status === "Watering") {
      clearTimeout(clearTimeoutVariable);
      this.setState({
        plantInfo: this.state.plantInfo.map((el) =>
          el.id === id
            ? {
                id: el.id,
                plantName: el.plantName,
                timestamp: Date.now(),
                plantStatus: "Just Watered",
              }
            : el
        ),
      });

      const toSetState = () => {
        this.setState({
          plantInfo: this.state.plantInfo.map((el) =>
            el.id === id
              ? {
                  id: el.id,
                  plantName: el.plantName,
                  timestamp: Date.now(),
                  plantStatus: "Water",
                }
              : el
          ),
        });
      };
      setTimeout(toSetState, 30000);
    } else {
      this.setState({
        plantInfo: this.state.plantInfo.map((el) =>
          el.id === id
            ? {
                id: el.id,
                plantName: el.plantName,
                timestamp: Date.now(),
                plantStatus: "Watering",
              }
            : el
        ),
      });
    }
  };

  return (
    <div>
    <h1 style={{ display: "flex", justifyContent: "center" }}>
      ShipVista Plants
    </h1>
    <StatusBar />
    <div style={{ display: "flex", justifyContent: "space-evenly" }}>
      {this.state.plantInfo.map((plant) => (
        <div key={plant.id}>
          <Plants
            couler={this.state.couler}
            buttonLook={this.buttonLook}
            buttonClicks={this.buttonClicks}
            plant={plant}
          />
        </div>
      ))}
    </div>
  </div>
)
}

export default App
