import React, { Component } from "react";
import ListOfPlants from "../../components/ListOfPlants/ListOfPlants";
import StatusBar from "../../components/StatusBar/StatusBar";
// import axios from "axios";

export default class Home extends Component {
  state = {
    plantInfo: [],
    couler: "red"
  };
  componentDidMount() {
    this.setState({
      plantInfo: [
        { timestamp: 1618274087486, plantName: "Name1", id: 1 },
        { timestamp: 1618274087486, plantName: "Name2", id: 2 },
        { timestamp: 1618252487486, plantName: "Name3", id: 3 },
        { timestamp: 1618274087486, plantName: "Name4", id: 4 },
        { timestamp: 1618274087486, plantName: "Name5", id: 5 },
      ],
    });
    // axios
    //   .get("https://localhost:5001/api/SVPlants")
    //   .then((res) =>
    //     this.setState({
    //       plantInfo: res.data,
    //     })
    //   )
    //   .catch((err) => console.error(err));
  }
  buttonClicks = (time, id) => {
    const time_subtraction = Date.now() - time;
    if (time_subtraction < 30000) {
      this.setState((prevState) => ({
        plantInfo: prevState.plantInfo.map((el) =>
          el.id === id ? { ...el, timestamp: Date.now() } : el
        ),
      }));
      console.log("Watering");
    } else if (time_subtraction > 21600000) {
      this.setState((prevState) => ({
        plantInfo: prevState.plantInfo.map((el) =>
          el.id === id ? { ...el, timestamp: Date.now() } : el
        ),
      }));
      console.log("Must Water");
    } else {
      this.setState((prevState) => ({
        plantInfo: prevState.plantInfo.map((el) =>
          el.id === id ? { ...el, timestamp: Date.now() } : el
        ),
      }));
      console.log(this.state.plantInfo);
      console.log("Water");
    }
  };
  buttonLook = (time, id) => {
    const time_subtraction = Date.now() - time;
    if (time === 1618274087486) {

      return (
        <button
          // disabled
          style={{
            backgroundColor: "pink",
            borderRadius: 5,
            border: "none",
            outline: "none",
            fontWeight: "bold",
            color: "white",
            width: "100%",
            fontSize: "20px",
            height: "30px",
          }}
          onClick={() => this.buttonClicks(time, id)}
        >
          Start Watering
        </button>
      );
    } else if (time_subtraction > 21600000) {
      return (
        <button
          style={{
            backgroundColor: "red",
            borderRadius: 5,
            border: "none",
            outline: "none",
            fontWeight: "bold",
            color: "white",
            width: "100%",
            fontSize: "13.5px",
            height: "30px",
          }}
          onClick={() => this.buttonClicks(time, id)}
        >
          Start Watering
        </button>
      );
    } else {
      return (
        <button
          style={{
            backgroundColor: "black",
            borderRadius: 5,
            border: "none",
            outline: "none",
            fontWeight: "bold",
            color: "white",
            width: "100%",
            fontSize: "20px",
            height: "30px",
          }}
          onClick={() => this.buttonClicks(time, id)}
        >
          Water
        </button>
      );
    }
  };

  render() {
    return (
      <div>
        <StatusBar />
        <div style={{ display: "flex", justifyContent: "space-evenly" }}>
          {this.state.plantInfo.map((plant) => (
            <ListOfPlants couler={this.state.couler} buttonLook={this.buttonLook} buttonClicks={this.buttonClicks} plant={plant} />
          ))}
        </div>
      </div>
    );
  }
}
