import React from 'react'
// import {
//   BrowserRouter as Router,
//   Switch,
//   Route,
//   Redirect,
// } from 'react-router-dom'

// import { Navbar } from './app/Navbar'

function App() {
  buttonClicks = (time, id, status) => {
    const aPlant = {
      plantName: "Plant 1",
      plantStatus: "MustWater",
      timestamp: 1234567890,
    };
    axios
      .put("http://localhost:2238/api/SVPlantItems/1", aPlant)
      .then((response) => console.log(response));

    const time_subtraction = Date.now() - time;
    // do an enumerate for the status options
    if (time_subtraction < 30000 && status === "Just Watered") {
      return null;
    } else if (status === "Must Water" || status === "Water") {
      this.setState({
        plantInfo: this.state.plantInfo.map((el) =>
          el.id === id
            ? {
                id: el.id,
                plantName: el.plantName,
                timestamp: Date.now(),
                plantStatus: "Watering",
              }
            : el
        ),
      });
      const tenSecondInterval = () => {
        this.setState({
          plantInfo: this.state.plantInfo.map((el) =>
            el.id === id
              ? {
                  id: el.id,
                  plantName: el.plantName,
                  timestamp: Date.now(),
                  plantStatus: "Just Watered",
                }
              : el
          ),
        });

        const toSetState = () => {
          this.setState({
            plantInfo: this.state.plantInfo.map((el) =>
              el.id === id
                ? {
                    id: el.id,
                    plantName: el.plantName,
                    timestamp: Date.now(),
                    plantStatus: "Water",
                  }
                : el
            ),
          });
        };

        setTimeout(toSetState, 30000);
      };
      clearTimeoutVariable = setTimeout(tenSecondInterval, 10000);
    } else if (time_subtraction < 10000 && status === "Watering") {
      clearTimeout(clearTimeoutVariable);
      this.setState({
        plantInfo: this.state.plantInfo.map((el) =>
          el.id === id
            ? {
                id: el.id,
                plantName: el.plantName,
                timestamp: Date.now(),
                plantStatus: "Just Watered",
              }
            : el
        ),
      });

      const toSetState = () => {
        this.setState({
          plantInfo: this.state.plantInfo.map((el) =>
            el.id === id
              ? {
                  id: el.id,
                  plantName: el.plantName,
                  timestamp: Date.now(),
                  plantStatus: "Water",
                }
              : el
          ),
        });
      };
      setTimeout(toSetState, 30000);
    } else {
      this.setState({
        plantInfo: this.state.plantInfo.map((el) =>
          el.id === id
            ? {
                id: el.id,
                plantName: el.plantName,
                timestamp: Date.now(),
                plantStatus: "Watering",
              }
            : el
        ),
      });
    }
  };

  return (
    <Router>
      <Navbar />
      <div className="App">
        <Switch>
          <Route
            exact
            path="/"
            render={() => (
              <section>
                <h2>Welcome to the Redux Essentials example app!</h2>
              </section>
            )}
          />
          <Redirect to="/" />
        </Switch>
      </div>
    </Router>
  )
}

export default App
