import React, { Component } from "react";
import ListOfPlants from "../../components/ListOfPlants/ListOfPlants";
import StatusBar from "../../components/StatusBar/StatusBar";
// import axios from "axios";

export default class Home extends Component {
  state = {
    plantInfo: [],
  };
  componentDidMount() {
    this.setState({
      plantInfo: [
        { timestamp: 1618274087486, plantName: "Name1", id: 1 },
        { timestamp: 1618274087486, plantName: "Name2", id: 2 },
        { timestamp: 1618252487486, plantName: "Name3", id: 3 },
        { timestamp: 1618274087486, plantName: "Name4", id: 4 },
        { timestamp: 1618274087486, plantName: "Name5", id: 5 },
      ],
    });
    // axios
    //   .get("https://localhost:5001/api/SVPlants")
    //   .then((res) =>
    //     this.setState({
    //       plantInfo: res.data,
    //     })
    //   )
    //   .catch((err) => console.error(err));
  }
  buttonClicks = (time, id) => {
    const time_subtraction = Date.now() - time;
    if (time_subtraction < 30000) {
      this.setState((prevState) => ({
        plantInfo: prevState.plantInfo.map((el) =>
          el.id === id ? { ...el, timestamp: Date.now() } : el
        ),
      }));
      console.log("Watering");
    } else if (time_subtraction < 30000) {
      console.log("Must Water");
    } else {
      this.setState((prevState) => ({
        plantInfo: prevState.plantInfo.map((el) =>
          el.id === id ? { ...el, timestamp: Date.now() } : el
        ),
      }));
      console.log(this.state.plantInfo);
      console.log("Water");
    }
  };

  render() {
    return (
      <div>
        <StatusBar />
        <div style={{ display: "flex", justifyContent: "space-evenly" }}>
          {this.state.plantInfo.map((plant) => (
            <ListOfPlants buttonClicks={this.buttonClicks} plant={plant} />
          ))}
        </div>
      </div>
    );
  }
}
